  </template>
  
  <script>
  const { ipcRenderer } = window.require("electron");
  import { globalState, SetNodeType } from '../store';
  import { ref } from 'vue';
  import WindowHeader from './WindowHeader.vue'

    export default {
    components: {
        WindowHeader,
    },
    data() {
        return {
            node_type: "superlight"
        }
    },
    mounted() {
         const node_type = ref(globalState.node_type);
         if(node_type.value != "") {
            this.node_type = node_type.value;
         }
    },
    methods: {
      goToConfigure() {
        if(this.node_type == "") {
            return
        }

        SetNodeType(this.node_type);

        this.$router.push("configure_node");
      },
      goBack() {
        this.$router.back()
      },
      run() {
        ipcRenderer.send("run-ffg");
      }
    }
  }
  </script>
  
