  </template>
  
  <script>
  const { ipcRenderer } = window.require("electron");
  import { globalState, SetPassword } from '../store';
  import { ref } from 'vue';
  import WindowHeader from './WindowHeader.vue'

    export default {
    components: {
        WindowHeader,
    },
    data() {
        return {
            keypath: "",
            keypassword: "",
            importError: "",
            errorMessage: "",
            password: "",
            password2: ""
        }
    },
    mounted() {
         const password = ref(globalState.password);
         if(password.value != "") {
            this.password = password.value;
            this.password2 = password.value;
         }
    },
    methods: {
      selectedKeyPath(e) {
        try {
          this.keypath = e.target.files[0].path;
        } catch (e) {
          this.importError = e.message;
        }
      },
      importKey() {
        if(this.keypath == "") {
          this.importError = "Please choose a wallet key file first";
          return
        }

        if(this.keypassword == "") {
          this.importError = "Password is empty, please enter the password associated with this wallet key";
          return
        }

        this.importError = "";
        let st = ipcRenderer.sendSync("check-key", { path: this.keypath, password: this.keypassword });
        if(st.error != "") {
          this.importError = st.error;
        } else {
          this.password = this.keypassword;
          this.password2 = this.keypassword;
          this.goToNodeChooser()
        }
      },
      openUnlockModal() {
            this.subChannelCreated = false;
            const myModal = document.getElementById('modal-unlockKey');
            const modal = window.UIkit.modal(myModal);
            modal.show();
        },
      closeUnlockModal() {
          const myModal = document.getElementById('modal-unlockKey');
          const modal = window.UIkit.modal(myModal);
          modal.hide();
      },

      goToNodeChooser() {
        if(this.password == '' || this.password2 == '') {
            this.errorMessage = "Password can not be empty."
            return
        }

        if(this.password !== this.password2) {
            this.errorMessage = "Password confirmation doesn't match the password."
            return
        }

        SetPassword(this.password)

        this.$router.push("choose_node_type")
      },
      goBack() {
        this.$router.back()
      },
      run() {
        ipcRenderer.send("run-ffg");
      }
    }
  }
  </script>
  
