                    <div v-if="submitTransactionError != ''" style="margin-top:10px;">
                        <span class="uk-text-small uk-text-danger"> <span style="margin-right:5px;" uk-icon="icon: warning;"></span> {{ submitTransactionError }}</span>
                    </div>
                    
                </div>

            </div>
        </div>

        <div id="modal-receive" uk-modal>
            <div class="uk-modal-dialog uk-modal-body">
                <button class="uk-modal-close-default" type="button" uk-close></button>
                <h2 class="uk-modal-title">Receive FFG</h2>
                <div style="padding-bottom:10px; text-align: center; border-bottom:1px solid rgb(230, 230, 230);">
                    <img style="height: 72px; background-color: white; width: 72px; border-radius: 50%;" src="/assets/icon.png" />
                    <br />
                    <span class="uk-text-lead"> Wallet Address </span>
                </div>
                <div style="padding: 10px; margin-top:10px;">
                    <div style="width:100%;" class="uk-inline">
                        <span class="uk-form-icon" uk-icon="icon: world"></span>
                        <input v-model="nodeAddress" style="width:100%; border-radius: 4px;" class="uk-input" type="text" placeholder="Your Address" aria-label="Input">
                    </div>
                </div>
                <div style="padding: 10px; margin-top:10px;">
                    <button @click="copyAddrToClipboard" class="uk-button ffg-button" style="font-weight: bold; text-transform: none; width:100%; height:50px;">
                        Copy Address
                        <span class="uk-icon" uk-icon="icon:  copy"></span>
                    </button>
                    <br />
                    <div ref="copiedAddrAlert" v-show="addressCopied" class="uk-alert-success" uk-alert>
                        <a class="uk-alert-close" uk-close></a>
                        <p>Address was copied successfully.</p>
                    </div>
                </div>
            </div>
        </div>

        <div id="modal-txinfo" uk-modal>
            <div class="uk-modal-dialog uk-modal-body">
                <button class="uk-modal-close-default" type="button" uk-close></button>
                <h2 class="uk-modal-title">Transaction Info</h2>
                <div style="color:#000; word-wrap: break-word;">
                    <div v-if="tmpTXInfo != null" style="padding:5px;">
                        <span style="color: #000; font-weight: bold;">Block: </span> {{tmpTXInfo.block_number}}
                        <br /> <br />
                        <span style="color: #000; font-weight: bold;">Time: </span> {{formatTimestampHuman(tmpTXInfo.timestamp)}}
                        <br /> <br />
                        <span style="color: #000; font-weight: bold;">Nounce: </span> {{tmpTXInfo.transaction.nounce}}
                        <br /> <br />
                        <span style="color: #000; font-weight: bold;">From: </span> {{tmpTXInfo.transaction.from}}
                        <br /> <br />
                        <span style="color: #000; font-weight: bold;">To: </span> {{tmpTXInfo.transaction.to}}
                        <br /> <br />
                        <span style="color: #000; font-weight: bold;">Value: </span> {{formatAmount(tmpTXInfo.transaction.value)}} FFG
                        <br /> <br />
                        <span style="color: #000; font-weight: bold;">Fees: </span> {{formatAmount(tmpTXInfo.transaction.transaction_fees)}} FFG
                        <br /> <br />
                        <span style="color: #000; font-weight: bold;">Hash: </span>  {{tmpTXInfo.transaction.hash}}
                        <br /> <br />
                    </div>
                </div>
            </div>
        </div>

        <div class="uk-flex-top" id="modal-confirmation" uk-modal>
            <div class="uk-modal-dialog uk-modal-body  uk-margin-auto-vertical">
                <h3 style="font-size: 21px;" class="uk-modal-title uk-text-default">Transaction was sent</h3>
                <p style="color:#000;">
                    Your transaction was successfully sent, please wait for the confirmation.
                </p>
                <p class="uk-text-right">
                    <button style="border-radius: 4px;" class="uk-button uk-button-default uk-modal-close" type="button">Close</button>
                </p>
            </div>
        </div>
</div>
</div>
</template>
      
<script>
import axios from 'axios';
import { globalState} from '../../store';
import { ref, nextTick} from 'vue';
import { Units } from "../../unit.js"
import BigNumber from 'bignumber.js';
import numberToBN from "number-to-bn";
import { callJsonRpc2Endpoint } from '../../rpc'
const { ipcRenderer } = window.require("electron");

export default {
    components: {
    },
    data() {
        return {
            lastTXSent : "",
            loadingIntervalError: "",
            loadingInterval: false,
            tmpTXInfo: null,
            loadingTransactions: false,
            reloadInterval: null,
            transactions: [],
            submitTx: false,
            nodeAddress: "",
            balance: "0",
            usdPerFFG: "0.05",
            convertToUSD: "0",
            addressCopied: false,
            toAddress: "",
            toAmount: "",
            transactionFees: "0.001",
            validateAddressError: "",
            validateAmountError: "",
            validateTransactionFeesError: "",
            submitTransactionError: ""
        }
    },
    watch: {
        toAddress(newVal) {
            if(!this.validateAddress(newVal)) {
                this.validateAddressError = "Address is invalid";
            } else {
                this.validateAddressError = "";
            }
        },
        toAmount(newVal) {
            if(!this.validateFFG(newVal)) {
                this.validateAmountError = "Amount is invalid";
            } else {
                this.validateAmountError = "";
            }
        },
        transactionFees(newVal) {
            if(!this.validateFFG(newVal)) {
                this.validateTransactionFeesError = "Transaction fees is invalid";
            } else {
                this.validateTransactionFeesError = "";
            }
        }
    },
    unmounted () {
        if(this.reloadInterval != null) {
            clearInterval(this.reloadInterval)
        }
    },
    async mounted() {
        this.reloadInterval  = setInterval(this.reloadTransactions , 10000);
        const addr = ref(globalState.nodeAddress);
        if(addr.value != "") {
            this.nodeAddress = addr.value;
        }

        try {
            this.loadingTransactions = true;
            let txRes = await this.getTransactions();
            this.transactions = txRes;
            let balanceRes = await this.getBalance();

            let balanceBig = new BigNumber(balanceRes.balance_hex, 16);
            let balanceInFFG = Units.convert(balanceBig.toString(10), "FFGOne", "FFG")
            this.balance = new BigNumber(balanceInFFG, 10).toString(10)
            let parts = this.balance.split(".");
            this.convertToUSD = this.formatMoney(Number(parts[0]) * this.usdPerFFG)
        } catch (e) {
            alert(e.message);
        } finally {
            this.loadingTransactions = false;
        }


    },
    methods: {
        formatTimestampHuman(unixTimestamp) {
            const date = new Date(unixTimestamp * 1000); 
            const year = date.getFullYear();
            const month = ('0' + (date.getMonth() + 1)).slice(-2);
            const day = ('0' + date.getDate()).slice(-2); 
            const hours = ('0' + date.getHours()).slice(-2);
            const minutes = ('0' + date.getMinutes()).slice(-2); 
            const seconds = ('0' + date.getSeconds()).slice(-2);
            return `${day}/${month}/${year} ${hours}:${minutes}:${seconds}`;
        },
        formatTimestamp(timestamp) {
            const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
            const date = new Date(timestamp * 1000);
            const month = months[date.getMonth()];
            const day = date.getDate();
            return { month: month, day: day };
        },
        openTxModal(tx) {
            this.tmpTXInfo = tx;
            const myModal = document.getElementById('modal-txinfo')
            const modal = window.UIkit.modal(myModal)
            modal.show()
        },
        formatAmount(amount) {
            let amountBig = new BigNumber(amount, 16);
            return Units.convert(amountBig.toString(10), "FFGOne", "FFG")
        },
        formatMoney(amount) {
            const formatter = new Intl.NumberFormat('en-US', {
                style: 'decimal',
                minimumFractionDigits: 2
            });
            return formatter.format(amount);
        },
        async getBalance() {
            const data = {
                jsonrpc: '2.0',
                method: "address.Balance",
                params: [{ address: this.nodeAddress}],
                id: 1
            };
            const endpoint = ref(globalState.rpcEndpoint);
            const response = await axios.post(endpoint.value, data);
            return response.data.result;
        },
        async getTransactions() {
            const data = {
                jsonrpc: '2.0',
                method: "transaction.ByAddress",
                params: [{ address: this.nodeAddress, current_page:0, page_size: 10 }],
                id: 1
            };

            const endpoint = ref(globalState.rpcEndpoint);
            const response = await axios.post(endpoint.value, data);
            return response.data.result.transactions;
        },
        async reloadTransactions() {
            try {
                this.loadingIntervalError = ""
                this.loadingInterval = true;
                let txRes = await this.getTransactions();
                this.transactions = txRes;
                let balanceRes = await this.getBalance();
                let balanceBig = new BigNumber(balanceRes.balance_hex, 16);
                let balanceInFFG = Units.convert(balanceBig.toString(10), "FFGOne", "FFG")
                this.balance = new BigNumber(balanceInFFG, 10).toString(10)
                let parts = this.balance.split(".");
                this.convertToUSD = this.formatMoney(Number(parts[0]) * this.usdPerFFG)
            } catch (e) {
               this.loadingIntervalError = e.message;
            } finally {
                this.loadingInterval = false
            }
        },
        buy() {
            let res = ipcRenderer.sendSync("openUrl", { url: "https://filefilego.com?buy" });
            if(res.error != "") {
                alert("Failed to open url");
            }
        },
        async sendTransaction() {
            try {

                if(!this.validateAddress(this.toAddress)) {
                    this.validateAddressError = "Address is invalid";
                } else {
                    this.validateAddressError = "";
                }

                if(!this.validateFFG(this.toAmount)) {
                    this.validateAmountError = "Amount is invalid";
                } else {
                    this.validateAmountError = "";
                }

                if(!this.validateFFG(this.transactionFees)) {
                    this.validateTransactionFeesError = "Transaction fees is invalid";
                } else {
                    this.validateTransactionFeesError = "";
                }

                if(this.validateAddressError != '' ||  this.validateAmountError != ''  || this.validateTransactionFeesError!= '' ) {
                    return;
                }

                this.submitTransactionError = "";

                if(this.submitTx) {
                    return;
                }

                this.submitTx = true;

                let valueFFGOneString = Units.convert(this.toAmount, "FFG", "FFGOne")
                let valueFFGOne = new BigNumber(valueFFGOneString, 10).toString(16)
                if (valueFFGOne == "0") {
                    throw new Error(
                    `Amount must be greater than zero`
                    );
                }

                let feesFFGOneString = Units.convert(this.transactionFees, "FFG", "FFGOne")
                let feesFFGOne = new BigNumber(feesFFGOneString, 10).toString(16)
                if (feesFFGOne == "0") {
                    throw new Error(
                    `Fees must be greater than zero`
                    );
                }

                if (this.toAddress === '') {
                    throw new Error(
                        `Address is empty`
                    );
                }

                if (this.toAddress.length != 42) {
                     throw new Error(
                        `Address is invalid`
                    );
                } 

                if (!this.toAddress.startsWith("0x")) {
                    throw new Error(
                        `Address should start with 0x`
                    );
                }

                let balanceRes = await this.getBalance();
                let balanceFFGOneBig = numberToBN(balanceRes.balance_hex);
                let valueBig = numberToBN("0x"+valueFFGOne);
                let feesBig = numberToBN("0x"+feesFFGOne);

                let totalFees = valueBig.add(feesBig);

                if(balanceFFGOneBig.lt(totalFees)) {
                    throw new Error(
                        `You don't have enough coins in your balance!`
                    );
                }
              
                const jwtAccess = ref(globalState.jwtAccessToken);              
                const response = await callJsonRpc2Endpoint("transaction.SendTransaction", [{ access_token: jwtAccess.value, nounce: balanceRes.next_nounce, data : "0x", from: this.nodeAddress , to: this.toAddress, value: "0x"+valueFFGOne, transaction_fees: "0x"+feesFFGOne }])
                this.lastTXSent = response.data.result.transaction.hash
                
                const myModal = document.getElementById('modal-send')
                const modal = window.UIkit.modal(myModal)
                modal.hide()

                const confirmModal = document.getElementById('modal-confirmation')
                const confirmModalVar = window.UIkit.modal(confirmModal)
                confirmModalVar.show()
                this.toAddress = "";
                this.toAmount = "";
                await nextTick()
                this.validateAddressError = "";
                this.validateAmountError = "";
                this.validateTransactionFeesError = "";
            } catch (e) {
                this.submitTransactionError = e.message;
            } finally {
                this.submitTx = false;
            }
        },
        validateFFG(value) {
            if (value === '') return false;        
            try {
                Units.convert(value, "FFG", "FFGOne")
                return true;
            } catch (e) {
                return false;
            }
        },
        validateAddress(value) {
            if (value.length != 42) {
                return false;
            } 

            if (!value.startsWith("0x")) {
                return false;
            }

            return true
        },
        async copyAddrToClipboard() {
            try {
                await navigator.clipboard.writeText(this.nodeAddress);
                this.addressCopied = true;
            } catch (err) {
                console.log(err.message)
                alert("failed to copy address")
            }
        }
    }
}
</script>

